mapper.select:
select O_NUM, O_ID, O_DATE, O_NAME, O_PH, O_ZIPCODE, O_ADDRESS1, O_ADDRESS2, O_STATUS, O_WAY, O_TOT, O_TNUM, O_IMP, O_POINT
from Orders
where O_NUM = #{o_num}

select OD_NUM, OD_CODE, OD_NAME, OD_DEVICE, OD_COLOR, OD_COUNT, OD_PRICE, OD_DISCOUNT
from ORDER_DETAIL
where OD_NUM = #{o_num}
